package Feb2lab;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=125;
		int b=24;
		
		System.out.println("Additon of two no is "+(a+b));
		System.out.println("Multiplication of two no is "+(a*b));
		System.out.println("Difference of two no is "+(a-b));
		System.out.println("Division of two no is "+(a/b));
		System.out.println("Remainder of two no is "+(a%b));


	}

}
